import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './StudentPage.css';

const StudentPage = ({ user, onLogout, onChangeRole }) => {
  const navigate = useNavigate();
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answer, setAnswer] = useState(null);
  const [submitted, setSubmitted] = useState(false);
  const [answeredCount, setAnsweredCount] = useState(0);

  useEffect(() => {
    // Get selected questions from sessionStorage (set by TeacherPage)
    const selectedQuestionsJSON = sessionStorage.getItem('selectedQuestions');
    if (selectedQuestionsJSON) {
      const selectedQuestions = JSON.parse(selectedQuestionsJSON);
      setQuestions(selectedQuestions);
    }
  }, []);

  if (questions.length === 0) {
    return (
      <div className="student-page">
        <div className="student-header">
          <button className="home-logo" onClick={() => navigate('/')} title="◊ó◊ñ◊®◊î ◊ú◊¢◊û◊ï◊ì ◊î◊ë◊ô◊™">üí°</button>
          <div className="header-nav">
            <button className="role-switch-btn" onClick={() => { onChangeRole('teacher'); navigate('/teacher'); }} title="◊¢◊û◊ï◊ì ◊î◊û◊ï◊®◊î">üë©‚Äçüè´</button>
            <button className="role-switch-btn" onClick={() => { onChangeRole('student'); navigate('/student'); }} title="◊¢◊û◊ï◊ì ◊î◊™◊ú◊û◊ô◊ì◊î">üëß</button>
            <button className="role-switch-btn" onClick={() => { onChangeRole('admin'); navigate('/admin'); }} title="◊ú◊ï◊ó ◊†◊ô◊î◊ï◊ú">üìä</button>
          </div>
          <div className="header-content">
            <h1>◊¢◊û◊ï◊ì ◊î◊™◊ú◊û◊ô◊ì◊î</h1>
            <p>◊î◊û◊ï◊®◊î ◊¢◊ì◊ô◊ô◊ü ◊ú◊ê ◊ë◊ó◊®◊î ◊©◊ê◊ú◊ï◊™. ◊ê◊†◊ê ◊ó◊õ◊ô...</p>
          </div>
          <button className="back-button" onClick={() => navigate('/')}>◊ó◊ñ◊®◊î</button>
        </div>
        <div className="student-container">
          <p style={{ textAlign: 'center', color: '#999' }}>‚è≥ ◊î◊û◊ï◊®◊î ◊™◊ë◊ó◊® ◊©◊ê◊ú◊ï◊™ ◊ë◊ß◊®◊ï◊ë...</p>
        </div>
      </div>
    );
  }

  const question = questions[currentQuestionIndex];

  const handleSubmitAnswer = () => {
    if (answer !== null) {
      setSubmitted(true);
      setAnsweredCount(answeredCount + 1);
      setTimeout(() => {
        if (currentQuestionIndex < questions.length - 1) {
          setCurrentQuestionIndex(currentQuestionIndex + 1);
          setAnswer(null);
          setSubmitted(false);
        } else {
          setSubmitted('completed');
        }
      }, 1500);
    }
  };

  const handleNavigate = (role) => {
    onChangeRole(role);
    navigate(`/${role}`);
  };

  const handleRestart = () => {
    sessionStorage.removeItem('selectedQuestions');
    onChangeRole('teacher');
    navigate('/teacher');
  };

  return (
    <div className="student-page">
      <div className="student-header">
        <button className="home-logo" onClick={() => navigate('/')} title="◊ó◊ñ◊®◊î ◊ú◊¢◊û◊ï◊ì ◊î◊ë◊ô◊™">üí°</button>
        <div className="header-nav">
          <button className="role-switch-btn" onClick={() => handleNavigate('teacher')} title="◊¢◊û◊ï◊ì ◊î◊û◊ï◊®◊î">üë©‚Äçüè´</button>
          <button className="role-switch-btn" onClick={() => handleNavigate('student')} title="◊¢◊û◊ï◊ì ◊î◊™◊ú◊û◊ô◊ì◊î">üëß</button>
          <button className="role-switch-btn" onClick={() => handleNavigate('admin')} title="◊ú◊ï◊ó ◊†◊ô◊î◊ï◊ú">üìä</button>
        </div>
        <div className="header-content">
          <h1>◊¢◊û◊ï◊ì ◊î◊™◊ú◊û◊ô◊ì◊î</h1>
          <p>◊ê◊†◊ê ◊¢◊†◊ï ◊ë◊õ◊†◊ï◊™ ◊ï◊ú◊ú◊ê ◊ú◊ó◊•</p>
        </div>
        <button className="back-button" onClick={() => navigate('/')}>◊ó◊ñ◊®◊î</button>
      </div>

      <div className="student-content">
        {submitted === 'completed' ? (
          <div className="completion-screen">
            <div className="success-animation">
              <div className="checkmark">‚úì</div>
            </div>
            <h2>◊™◊ï◊ì◊î! üéâ</h2>
