import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { mockTeacherQuestions } from '../services/mockData';
import './TeacherPage.css';

const TeacherPage = ({ user, onBack }) => {
  const navigate = useNavigate();
  const [questions, setQuestions] = useState(mockTeacherQuestions);
  const [newQuestion, setNewQuestion] = useState('');
  const [showForm, setShowForm] = useState(false);

  const handleAddQuestion = () => {
    if (newQuestion.trim()) {
      const question = {
        id: 'q' + Date.now(),
        text: newQuestion,
        responses: 0,
        understanding: 0,
        createdAt: new Date(),
        active: true
      };
      setQuestions([question, ...questions]);
      setNewQuestion('');
      setShowForm(false);
    }
  };

  const handleDeactivate = (id) => {
    setQuestions(questions.filter(q => q.id !== id));
  };

  const formatTime = (date) => {
    const now = new Date();
    const diff = now - date;
    const minutes = Math.floor(diff / 60000);
    if (minutes < 1) return 'כרגע';
    if (minutes < 60) return `לפני ${minutes} דקות`;
    const hours = Math.floor(minutes / 60);
    return `לפני ${hours} שעות`;
  };

  return (
    <div className="teacher-page">
      <header className="teacher-header">
        <div className="header-content">
          <button className="back-button" onClick={onBack}>← חזרה</button>
          <div>
            <h1>👩‍🏫 עמוד המורה</h1>
            <p>ברוכה חוזרת, {user?.name}</p>
          </div>
        </div>
      </header>

      <div className="teacher-container">
        <div className="question-creation-card">
          <div className="card-header">
            <h2>➕ שאלה חדשה</h2>
            <p>יצירת שאלה קצרה לסיום השיעור</p>
          </div>

          {!showForm ? (
            <button className="primary-button" onClick={() => setShowForm(true)}>
              + הוסף שאלה חדשה
            </button>
          ) : (
            <div className="question-form">
              <textarea
                placeholder="הקלידי את השאלה שלך..."
                value={newQuestion}
                onChange={(e) => setNewQuestion(e.target.value)}
                className="question-input"
                dir="rtl"
              />
              <div className="form-buttons">
                <button
                  className="primary-button"
                  onClick={handleAddQuestion}
                  disabled={!newQuestion.trim()}
                >
                  שלח לכיתה
                </button>
                <button
                  className="secondary-button"
                  onClick={() => {
                    setShowForm(false);
                    setNewQuestion('');
                  }}
                >
                  ביטול
                </button>
              </div>
            </div>
          )}
        </div>

        <div className="questions-section">
          <div className="section-header">
            <h2>📋 שאלות פעילות</h2>
            <span className="badge">{questions.length}</span>
          </div>

          {questions.length === 0 ? (
            <div className="empty-state">
              <p>🎯 אין שאלות פעילות כרגע</p>
              <p className="empty-hint">הוסף שאלה חדשה כדי להתחיל</p>
            </div>
          ) : (
            <div className="questions-list">
              {questions.map((q) => (
                <div key={q.id} className="question-card">
                  <div className="question-content">
                    <div className="question-text">
                      <p>{q.text}</p>
                      <span className="time-ago">{formatTime(q.createdAt)}</span>
                    </div>
                    <div className="question-stats">
                      <div className="stat">
                        <span className="stat-value">{q.responses}</span>
                        <span className="stat-label">תשובות</span>
                      </div>
                      {q.responses > 0 && (
                        <div className="stat">
                          <span className="stat-value">{q.understanding}%</span>
                          <span className="stat-label">הבנה</span>
                        </div>
                      )}
                    </div>
                  </div>
                  <button
                    className="action-button delete"
                    onClick={() => handleDeactivate(q.id)}
                    title="הסר שאלה"
                  >
                    ✕
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>

        <div className="tips-section">
          <h3>💡 טיפים למורה</h3>
          <ul>
            <li>✓ שאלה קצרה וברורה מקבלת יותר תשובות</li>
            <li>✓ שאל בסוף השיעור, לא בתחילתו</li>
            <li>✓ אנונימיות גורמת לתשובות כנות יותר</li>
            <li>✓ בדוק את הדשבורד לתובנות על הבנת הכיתה</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default TeacherPage;
