import React, { useState } from 'react';
import { mockQuestions, mockResponses } from '../services/mockData';
import './StudentPage.css';

const StudentPage = ({ user, onBack }) => {
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [answer, setAnswer] = useState(null);
  const [submitted, setSubmitted] = useState(false);
  const [answeredCount, setAnsweredCount] = useState(0);

  const question = mockQuestions[currentQuestionIndex];

  const handleSubmitAnswer = () => {
    if (answer !== null) {
      setSubmitted(true);
      setAnsweredCount(answeredCount + 1);
      setTimeout(() => {
        if (currentQuestionIndex < mockQuestions.length - 1) {
          setCurrentQuestionIndex(currentQuestionIndex + 1);
          setAnswer(null);
          setSubmitted(false);
        } else {
          setSubmitted('completed');
        }
      }, 1500);
    }
  };

  const handleNext = () => {
    setCurrentQuestionIndex(0);
    setAnswer(null);
    setSubmitted(false);
    setAnsweredCount(0);
  };

  return (
    <div className="student-page">
      <header className="student-header">
        <button className="back-button" onClick={onBack}>‚Üê ◊ó◊ñ◊®◊î</button>
        <h1>üëß ◊©◊ê◊ú◊™ ◊î◊û◊©◊ï◊ë</h1>
      </header>

      <div className="student-content">
        {submitted === 'completed' ? (
          <div className="completion-screen">
            <div className="success-animation">
              <div className="checkmark">‚úì</div>
            </div>
            <h2>◊™◊ï◊ì◊î! üéâ</h2>
            <p>◊î◊©◊ú◊û◊™ ◊ê◊™ ◊õ◊ú ◊î◊©◊ê◊ú◊ï◊™ ◊ë◊î◊¶◊ú◊ó◊î</p>
            <p className="response-summary">◊¢◊†◊ô◊™ ◊¢◊ú {answeredCount} ◊©◊ê◊ú◊ï◊™</p>
            <button className="primary-button" onClick={handleNext}>
              ◊ó◊ñ◊®◊î ◊ú◊™◊ó◊ô◊ú◊î
            </button>
          </div>
        ) : submitted ? (
          <div className="success-message">
            <h2>‚úì ◊™◊ï◊ì◊î!</h2>
            <p>◊î◊™◊©◊ï◊ë◊î ◊©◊ú◊ö ◊†◊®◊©◊û◊î ◊ë◊î◊¶◊ú◊ó◊î</p>
            {currentQuestionIndex < mockQuestions.length - 1 && (
              <p className="next-hint">◊î◊©◊ê◊ú◊î ◊î◊ë◊ê◊î ◊û◊í◊ô◊¢◊î...</p>
            )}
          </div>
        ) : (
          <div className="question-container">
            <div className="progress-bar">
              <div 
                className="progress-fill"
                style={{ width: `${((currentQuestionIndex + 1) / mockQuestions.length) * 100}%` }}
              />
            </div>
            <p className="progress-text">
              ◊©◊ê◊ú◊î {currentQuestionIndex + 1} ◊û◊™◊ï◊ö {mockQuestions.length}
            </p>

            <div className="question-box">
              <p className="question-text">{question.text}</p>
              
              <div className="answer-options">
                {question.questionType === 'yes-no' ? (
                  <>
                    <button
                      className={`option-btn yes ${answer === true ? 'selected' : ''}`}
                      onClick={() => setAnswer(true)}
                    >
                      <span className="icon">‚úì</span>
                      <span className="text">◊õ◊ü, ◊î◊ë◊†◊™◊ô</span>
                    </button>
                    <button
                      className={`option-btn no ${answer === false ? 'selected' : ''}`}
                      onClick={() => setAnswer(false)}
                    >
                      <span className="icon">‚úó</span>
                      <span className="text">◊ú◊ê, ◊ê◊†◊ô ◊¶◊®◊ô◊õ◊î ◊¢◊ñ◊®◊î</span>
                    </button>
                  </>
                ) : question.questionType === 'multiple-choice' ? (
                  question.options.map((option, idx) => (
                    <button
                      key={idx}
                      className={`option-btn choice ${answer === idx ? 'selected' : ''}`}
                      onClick={() => setAnswer(idx)}
                    >
                      {option}
                    </button>
                  ))
                ) : null}
              </div>
              
              <button
                className="submit-btn"
                onClick={handleSubmitAnswer}
                disabled={answer === null}
              >
                ◊©◊ú◊ô◊ó◊î ‚Üí
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default StudentPage;
