import React from 'react';
import './TeacherPage.css';

const TeacherPage = ({ user }) => {
  const [questions, setQuestions] = React.useState([]);
  const [newQuestion, setNewQuestion] = React.useState('');

  const handleAddQuestion = () => {
    if (newQuestion.trim()) {
      setQuestions([...questions, { id: Date.now(), text: newQuestion, responses: [] }]);
      setNewQuestion('');
    }
  };

  return (
    <div className="teacher-page">
      <header className="header">
        <h1>👩‍🏫 עמוד המורה</h1>
        <p>ברוכה חוזרת, {user.name}</p>
      </header>

      <div className="teacher-content">
        <div className="question-creation">
          <h2>יצירת שאלות חדשות</h2>
          <div className="question-form">
            <input
              type="text"
              placeholder="הקלידי את השאלה שלך..."
              value={newQuestion}
              onChange={(e) => setNewQuestion(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleAddQuestion()}
              dir="rtl"
            />
            <button onClick={handleAddQuestion}>הוספה</button>
          </div>
        </div>

        <div className="questions-list">
          <h2>שאלות פעילות</h2>
          {questions.length === 0 ? (
            <p>אין שאלות פעילות כרגע</p>
          ) : (
            questions.map((q) => (
              <div key={q.id} className="question-card">
                <p>{q.text}</p>
                <span className="response-count">תשובות: {q.responses.length}</span>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default TeacherPage;
